<?php// tinh chu vi va dien tich hinh chu nha$a = !empty($_POST['a']) && isset($_POST['a']) ? $_POST['a']:0;$b = isset($_POST['b']) && !empty($_POST['b']) ? $_POST['b']:0;function dien_tich ($a,$b){    return $a*$b;}function chu_vi ($a, $b){    return 2*($a+$b);}?><form action="#" method = "post">    <table>        <tr>            <td><label for="a">chi?u r?ng a:</label></td>            <td><label for=""></label><input type="number" name = "a" id=""></td>        </tr>        <tr>            <td><label for="a">chi?u d?i b:</label></td>            <td><input type="number" name = "b" id=""></td>        </tr>    </table>    <input type="submit" value="T?nh">    <h1>Di?n t?ch h?nh ch? nh?t l? <?= dien_tich($a,$b);?></h1>    <br>    <h1>Chu vi h?nh ch? nh?t l? <?= chu_vi($a,$b);?></h1>    </form>